foreach ($cell in "WT", "KO") {
  # 1 DSB graphs
  python 3_make_pptx/make_pptx.py -i "plots/graphs/individual/${cell}_sgA_R1_sense.png" "plots/graphs/individual/${cell}_sgA_R1_branch.png" "plots/graphs/individual/${cell}_sgA_R1_cmv.png" "plots/graphs/individual/${cell}_sgB_R2_sense.png" "plots/graphs/individual/${cell}_sgB_R2_branch.png" "plots/graphs/individual/${cell}_sgB_R2_cmv.png" "plots/graphs/combined/${cell}_sgA_R1_sense_branch.png" "plots/graphs/combined/${cell}_sgA_R1_sense_cmv.png" "plots/graphs/combined/${cell}_sgB_R2_sense_branch.png" "plots/graphs/combined/${cell}_sgB_R2_sense_cmv.png" -lab "sgRNA A\nSense" "sgRNA A\nBranchΔ" "sgRNA A\nCMVΔ" "sgRNA B\nSense" "sgRNA B\nBranchΔ" "sgRNA B\nCMVΔ" "sgRNA A\nSense & BranchΔ" "sgRNA A\nSense & CMVΔ" "sgRNA B\nSense & BranchΔ" "sgRNA B\nSense & CMVΔ" -ng 2 -nr 2 2 -nc 3 2 -o "${cell}_1DSB.pptx" --legends node_size freq_ratio_sense_cmv variation_type node_outline edge_type

  # 2 DSB graphs
  python 3_make_pptx/make_pptx.py -i "plots/graphs/individual/${cell}_sgAB_R1_sense.png" "plots/graphs/individual/${cell}_sgAB_R1_branch.png" "plots/graphs/individual/${cell}_sgAB_R1_cmv.png" "plots/graphs/individual/${cell}_sgAB_R2_sense.png" "plots/graphs/individual/${cell}_sgAB_R2_branch.png" "plots/graphs/individual/${cell}_sgAB_R2_cmv.png" "plots/graphs/combined/${cell}_sgAB_R1_sense_branch.png" "plots/graphs/combined/${cell}_sgAB_R1_sense_cmv.png" "plots/graphs/combined/${cell}_sgAB_R2_sense_branch.png" "plots/graphs/combined/${cell}_sgAB_R2_sense_cmv.png" -lab "sgRNA A & B\nForward strand\nSense" "sgRNA A & B\nForward strand\nBranchΔ" "sgRNA A & B\nForward strand\nCMVΔ" "sgRNA A & B\nReverse strand\nSense" "sgRNA A & B\nReverse strand\nBranchΔ" "sgRNA A & B\nReverse strand\nCMVΔ" "sgRNA A & B\nForward strand\nSense & BranchΔ" "sgRNA A & B\nForward strand\nSense & CMVΔ" "sgRNA A & B\nReverse strand\nSense & BranchΔ" "sgRNA A & B\nReverse strand\nSense & CMVΔ" -ng 2 -nr 2 2 -nc 3 2 -o "${cell}_2DSB.pptx" --legends node_size freq_ratio_sense_cmv variation_type node_outline edge_type

  # Antisense graphs
  if ($cell -eq "WT") {
    python 3_make_pptx/make_pptx.py -i "plots/graphs/individual/${cell}_sgCD_R1_antisense.png" "plots/graphs/individual/${cell}_sgCD_R1_splicing.png" "plots/graphs/individual/${cell}_sgCD_R1_antisense.png" "plots/graphs/individual/${cell}_sgCD_R2_splicing.png" "plots/graphs/combined/${cell}_sgCD_R1_antisense_splicing.png" "plots/graphs/combined/${cell}_sgCD_R2_antisense_splicing.png" -lab "sgRNA C & D\nForward strand\nAntisense" "sgRNA C' & D\nForward strand\n5' splicingΔ" "sgRNA C & D\nForward strand\nAntisense" "sgRNA C' & D\nForward strand\n5' splicingΔ" "sgRNA C/C' & D\nForward strand\nAntisense & 5' splicingΔ" "sgRNA C/C' & D\nReverse strand\nAntisense & 5' splicingΔ" -ng 2 -nr 2 1 -nc 2 2 -o "${cell}_2DSBanti.pptx" --legends node_size freq_ratio_sense_cmv variation_type node_outline edge_type
  }
}
